import React from 'react';
import { motion } from 'framer-motion';

const About: React.FC = () => {
  return (
    <section id="about" className="py-20 bg-background-light">
      <div className="max-w-4xl mx-auto px-5">
        <motion.div
          className="relative"
          initial={{ opacity: 0, y: 50 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
          viewport={{ once: true }}
        >
          <div className="text-center">
            <motion.h2
              className="text-4xl md:text-5xl font-bold text-primary-gold uppercase tracking-wider mb-12"
              initial={{ opacity: 0, y: -20 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.6, delay: 0.2 }}
              viewport={{ once: true }}
            >
              About Me
            </motion.h2>
            <motion.p
              className="text-lg md:text-xl text-text-dark leading-relaxed max-w-3xl mx-auto font-semibold"
              initial={{ opacity: 0, y: 30 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.6, delay: 0.4 }}
              viewport={{ once: true }}
              style={{
                textShadow: '2px 2px 4px rgba(0, 0, 0, 0.1)'
              }}
            >
              With <span className="text-primary-gold font-bold">7+ years</span> of experience in communication and audiovisual production, I transform creative concepts into compelling visual narratives. Holding a <span className="text-primary-gold font-bold">Master's degree</span> from EFAP Lyon in Communication and Audiovisual Production, I specialize in managing complex projects from inception to delivery, coordinating technical and creative teams with precision while ensuring budget efficiency and fostering collaborative environments where innovation thrives.
            </motion.p>
          </div>
        </motion.div>
      </div>
    </section>
  );
};

export default About;
