import React from 'react';
import { motion } from 'framer-motion';

const About: React.FC = () => {
  return (
    <section id="about" className="py-20 bg-background-light">
      <div className="max-w-4xl mx-auto px-5">
        <motion.div
          className="relative"
          initial={{ opacity: 0, y: 50 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
          viewport={{ once: true }}
        >
          <div className="absolute inset-0 bg-gradient-to-r from-primary-gold/5 to-secondary-gold/5 rounded-3xl blur-3xl"></div>
          <div className="relative bg-white/80 backdrop-blur-sm rounded-3xl p-12 shadow-xl border border-primary-gold/10">
            <motion.div
              className="absolute -top-8 left-1/2 transform -translate-x-1/2"
              initial={{ opacity: 0, scale: 0 }}
              whileInView={{ opacity: 1, scale: 1 }}
              transition={{ duration: 0.6, delay: 0.3 }}
              viewport={{ once: true }}
            >
              <div className="bg-primary-gold text-white px-6 py-2 rounded-full text-sm font-bold uppercase tracking-wider">
                About Me
              </div>
            </motion.div>
            <motion.p
              className="text-lg md:text-xl text-text-dark leading-relaxed max-w-3xl mx-auto font-bold text-center"
              initial={{ opacity: 0, y: 30 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.6, delay: 0.4 }}
              viewport={{ once: true }}
            >
              With <span className="text-primary-gold">7+ years</span> of experience in communication and audiovisual production, I transform creative concepts into compelling visual narratives. Holding a <span className="text-primary-gold">Master's degree</span> from EFAP Lyon in Communication and Audiovisual Production, I specialize in managing complex projects from inception to delivery, coordinating technical and creative teams with precision while ensuring budget efficiency and fostering collaborative environments where innovation thrives.
            </motion.p>
          </div>
        </motion.div>
      </div>
    </section>
  );
};

export default About;
