import React from 'react';
import { motion } from 'framer-motion';

const About: React.FC = () => {
  return (
    <section id="about" className="py-32 bg-background-light">
      <div className="max-w-7xl mx-auto px-5">
        <motion.h2
          className="text-5xl font-bold text-center mb-20 text-primary-gold uppercase tracking-wider"
          initial={{ opacity: 0, y: 50 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6 }}
          viewport={{ once: true }}
        >
          About Me
        </motion.h2>

        <div className="grid grid-cols-1 lg:grid-cols-2 gap-16 items-center">
          <motion.div
            className="order-2 lg:order-1"
            initial={{ opacity: 0, x: -50 }}
            whileInView={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.6, delay: 0.2 }}
            viewport={{ once: true }}
          >
            <p className="text-xl md:text-2xl text-text-dark leading-relaxed font-medium mb-8">
              With <span className="text-primary-gold font-bold underline decoration-2 underline-offset-4">7+ years</span> of experience in communication and audiovisual production, I transform creative concepts into compelling visual narratives.
            </p>
            <p className="text-lg md:text-xl text-text-dark leading-relaxed mb-8">
              Holding a <span className="text-primary-gold font-bold">Master's degree</span> from EFAP Lyon in Communication and Audiovisual Production, I specialize in managing complex projects from inception to delivery, coordinating technical and creative teams with precision while ensuring budget efficiency and fostering collaborative environments where innovation thrives.
            </p>
            <div className="flex flex-wrap gap-4 mt-12">
              <div className="bg-primary-gold/10 border border-primary-gold/30 rounded-lg px-6 py-3">
                <p className="text-primary-gold font-semibold">Project Management</p>
              </div>
              <div className="bg-primary-gold/10 border border-primary-gold/30 rounded-lg px-6 py-3">
                <p className="text-primary-gold font-semibold">Team Coordination</p>
              </div>
              <div className="bg-primary-gold/10 border border-primary-gold/30 rounded-lg px-6 py-3">
                <p className="text-primary-gold font-semibold">Budget Optimization</p>
              </div>
              <div className="bg-primary-gold/10 border border-primary-gold/30 rounded-lg px-6 py-3">
                <p className="text-primary-gold font-semibold">Creative Strategy</p>
              </div>
            </div>
          </motion.div>

          <motion.div
            className="order-1 lg:order-2"
            initial={{ opacity: 0, x: 50 }}
            whileInView={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.6, delay: 0.3 }}
            viewport={{ once: true }}
          >
            <div className="relative">
              <div className="absolute inset-0 bg-gradient-to-r from-primary-gold/20 to-secondary-gold/20 rounded-3xl blur-2xl"></div>
              <div className="relative bg-gradient-to-br from-primary-gold/10 to-secondary-gold/10 rounded-3xl p-16 text-center">
                <div className="text-8xl font-bold text-primary-gold mb-4">7+</div>
                <div className="text-2xl font-semibold text-text-dark mb-2">Years of Experience</div>
                <div className="text-lg text-text-light">In Communication & Production</div>
              </div>
            </div>
          </motion.div>
        </div>
      </div>
    </section>
  );
};

export default About;
