import React from 'react';
import { motion } from 'framer-motion';

const About: React.FC = () => {
  return (
    <section id="about" className="py-20 bg-background-light">
      <div className="max-w-4xl mx-auto px-5">
        <motion.div
          className="text-center"
          initial={{ opacity: 0, y: 50 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
          viewport={{ once: true }}
        >
          <motion.p
            className="text-lg md:text-xl text-text-dark leading-relaxed max-w-3xl mx-auto"
            initial={{ opacity: 0, y: 30 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6, delay: 0.2 }}
            viewport={{ once: true }}
          >
            With 7+ years of experience in communication and audiovisual production, I transform creative concepts into compelling visual narratives. Holding a Master's degree from EFAP Lyon in Communication and Audiovisual Production, I specialize in managing complex projects from inception to delivery, coordinating technical and creative teams with precision while ensuring budget efficiency and fostering collaborative environments where innovation thrives.
          </motion.p>
        </motion.div>
      </div>
    </section>
  );
};

export default About;
